package com.example.bindingadapterprac.viewmodel

import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel
import com.example.bindingadapterprac.R
import com.example.bindingadapterprac.model.ProfileData

/**
 * 뷰모델에서 리사이클러뷰에 띄워줄 값을 가지고 있는 userList를 Livedata로 생성한다.
 * 선언시에 불러올 데이터 타입과 같은 리스트 타입으로 선언해준다.
 */
class MainViewModel: ViewModel() {
    private val _userList = MutableLiveData<ArrayList<ProfileData>>()
    val userList: LiveData<ArrayList<ProfileData>>
        get() = _userList

    // 테스트용 값들
    private var num = 1
    private val url = R.drawable.dog

    private val url2 = "data:image/jpeg;base64,"


    // 리스트에 임의로 값 추가
    private val items = arrayListOf(
        ProfileData("KJY", 29, url),
        ProfileData("KDH", 30, url)
    )

    // 라이브데이터 값 초기화
    init {
        _userList.value = items
    }

    fun onClick() {
        val user = ProfileData("Test $num", 20, url2)
        items.add(user)
        _userList.value = items
        num++
    }
}